mapper.update:
UPDATE tbl_user SET name=#{name}
, nickname=#{nickname}
, phonenumber=#{phonenumber}
, sex=#{sex}
, website=#{website}
, intro=#{intro}
, userlevel=#{userlevel}
WHERE id=#{id}

mapper.insert:
		INSERT INTO tbl_follow (userid, followedid)
		SELECT #{loginid}, #{userid} from DUAL
        WHERE NOT EXISTS(select * from tbl_follow where userid=#{loginid} and followedid=#{userid});

mapper.delete:
DELETE
FROM tbl_follow
WHERE userid=#{loginid}
AND
followedid=#{userid}

mapper.select:
select PASSWORD
FROM  tbl_user
WHERE EMAIL = #{email}

mapper.update:
update tbl_user
set password = #{password}
where email = #{email}

